#!/usr/bin/env python3                                                                                                  import glob                                                                                                             from PIL import Image                                                                                                   import os                                                                                                               def converter(loc):                                                                                                             im=Image.open(loc)                                                                                                      rotated=im.rotate(90)                                                                                                   out=rotated.resize((128,128))                                                                                           base = os.path.basename(loc)                                                                                            out=out.convert('RGB')                                                                                                  out.save("/opt/icons/"+base+".jpeg")                                                                                    print(loc+" is converted")                                                                                      if __name__ == "__main__":                                                                                                      images=(glob.glob("images/ic*"))                                                                                        for img in images:                                                                                                              converter(img)                                                                                                                                                                                                                                                                                                                                                       
